import styled from "styled-components";
//Estilo del boton
export const StyledStartButton = styled.div`
  box-sizing: border-box;
  margin: 0 0 20px 0;
  text-align: center;
  padding: 20px;
  min-height: 30px;
  width: 100%;
  border-radius: 20px;
  border: none;
  color: white;
  background: #333;
  font-family: Pixel, Arial, Helvetica, sans-serif;
  font-size: 1rem;
  outline: none;
  cursor: pointer;
`;
