import styled from "styled-components";
/** Estilos de la pantalla */
export const StyledStage = styled.div`
  display: grid;
  grid-template-columns: repeat(12, 1fr);
  gap: 1px;
  border: 2px solid rgb(51, 51, 51);
  width: 100%;
  max-width: 25vw;
  background: rgb(17, 17, 17);
  POSITION: absolute;
  min-width: 18rem;
  min-height: 32rem;
`;
