import styled from "styled-components";
//Estilo del boton
export const StyledPauseButton = styled.div`
  box-sizing: border-box;
  margin: 0 0 20px 0;
  text-align: center;
  padding: 20px;
  height: 4em;
  width: 100%;
  border-radius: 20px;
  border: none;
  color: white;
  background: #333;
  font-family: Pixel, Arial, Helvetica, sans-serif;
  font-size: 1rem;
  outline: none;
  cursor: pointer;
  box-shadow: 3px 4px 7px 0px grey;
`;
